import { useState, useEffect} from 'react'
import { NavLink } from 'react-router-dom'
import styles from './Menu.module.css'

  const Menu = () => {
    const [isNavSticky, setIsNavSticky] = useState(false);
    useEffect(() => {
      const handleScroll = () => {
        const scrollTop = window.scrollY;
  
        // Установите условие, при котором навигация становится прикрепленной
        setIsNavSticky(scrollTop > 100); // Пример: при прокрутке на 100px сверху
  
        // Дополнительные действия, если необходимо
      };
  
      // Добавьте обработчик события прокрутки при монтировании компонента
      window.addEventListener('scroll', handleScroll);
  
      // Удалите обработчик события прокрутки при размонтировании компонента
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);
  
    return (
      <nav className={isNavSticky ? 'sticky' : ''} >
        <NavLink
          end
          to="."
          className={styles.menuLink}
          // activeClassName={styles.menuLink.active}
        >
          Home
        </NavLink>
        <NavLink to="rent" className={styles.menuLink}>
          Rent
        </NavLink>
        <NavLink to="buysell" className={styles.menuLink}>
          BuySell
        </NavLink>
        <NavLink to="housing" className={styles.menuLink}>
          Housing
        </NavLink>
        <NavLink to="commercial" className={styles.menuLink}>
          Commercial
        </NavLink>
        <NavLink to="architecture" className={styles.menuLink}>
          Architecture
        </NavLink>
      </nav>
    );
}

export default Menu
