import { Link } from 'react-router-dom';
import { useState } from 'react';
import './Post.css';

const Post = ({ post }) => {
  const { imageUrl, title, text } = post;
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <Link to={`/posts/;${post.title.replace(/\s+/g, '-')}`} className="post-link" onClick={toggleExpand}>
      <div className="post">
        <h3>{title}</h3>
        <img src={imageUrl} alt={title} className="post-image" />
        <p>{isExpanded ? text : `${text.slice(0, 200)}...`}</p>
      </div>
    </Link>
  );
}

export default Post;
