const LinkedInShareButton = ({ postUrl}) => {
    const handleLinkedInShare = () => {
      // Предоставьте пользователю ссылку с метаданными вашего поста
      const linkedInShareUrl = `https://www.linkedin.com/shareArticle?url=${encodeURIComponent(postUrl)}&title=${encodeURIComponent(title)}&summary=${encodeURIComponent(summary)}`;
      
      // Откройте ссылку в новом окне или в текущем окне
      window.open(linkedInShareUrl, '_blank');
    };
  
    return (
      <button onClick={handleLinkedInShare}>
        Share on LinkedIn
      </button>
    );
  };

