import { Link } from 'react-router-dom';
import { useState } from 'react';
import styles from './Post.module.css'

const Post = ({ post }) => {
  const { imageUrl, title, text } = post;
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

 

  return (
    <Link to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`} className={styles.postLink} onClick={toggleExpand}>
      <div className={styles.post}>
        <h3>{title}</h3>
        <img src={imageUrl} alt={title} className={styles.postImage} />
        <p>{isExpanded ? text : `${text.slice(0, 200)}...`}</p>
        <div className={styles.shareButtons}>
          <button onClick={shareOnInstagram}>Share on Instagram</button>
          <button onClick={shareOnTelegram}>Share on Telegram</button>
          <button onClick={shareOnFacebook}>Share on Facebook</button>
          <button onClick={shareByEmail}>Share via Email</button>
        </div>
      </div>
    </Link>
  );
}

export default Post;
