import React from 'react';
import { useParams } from 'react-router-dom';
import { FaFacebook, FaTelegram, FaWhatsapp } from 'react-icons/fa';
import { SocialIcon } from 'react-social-icons';
import { FacebookShareButton, TelegramShareButton, WhatsappShareButton, LinkedinIcon, } from 'react-share';
import posts from '../Posts/postsData';
import styles from './FullPost.module.css'

const FullPost = () => {
  const { postId } = useParams();
 

  // Преобразуем postId к числу
  const selectedPost = posts.find(post => post.id === parseInt(postId));

  console.log('selectedPost:', selectedPost); 

  if (!selectedPost) {
    return <h3>Post not found</h3>;
  }

  const { title, imageUrl, text, date } = selectedPost;

  const shareUrl = window.location.href;
  const shareTitle = `Check out this post: ${title}`;

  return (
    <div className={styles.fullPostWrapper}>
 <div className={styles.fullPostContent}>
      <h3>{title}</h3>
      <h4>{date}</h4>
      <img src={imageUrl} alt={title} className={styles.fullPostImage} />
      <p>{text}</p>
      <div className={styles.shareButtons}>
          <SocialIcon url={shareUrl} quote={shareTitle}>
            <button className={styles.shareButton}>
              <FaFacebook/>
              </button>
          </SocialIcon>
          <TelegramShareButton url={shareUrl} title={shareTitle}>
            <button className={styles.shareButton}>
              <FaTelegram/>
            </button>
          </TelegramShareButton>
          <WhatsappShareButton url={shareUrl} title={shareTitle}>
          <button className={styles.shareButton}>
            <FaWhatsapp/>
          </button>
          </WhatsappShareButton>
        </div>
    </div>
    </div>
   
  );
}

export default FullPost;
