const InstagramShareButton = ({ postUrl }) => {
  const handleInstagramShare = () => {
    const instagramShareUrl = `https://www.instagram.com/?url=${encodeURIComponent(postUrl)}`;
    window.open(instagramShareUrl, '_blank');
  };

  return (
    <button onClick={handleInstagramShare}>
      Share on Instagram
    </button>
  );
};

export default InstagramShareButton;
