import {useState} from 'react';
import Post from '../Post/Post';
import Input from '../Input/Input';
import styles from './Posts.module.css'
import posts from './postsData'; // Импортируем массив постов

  const Posts = () => {
    const [searchText, setSearchText] = useState('');
   
    const handleSearch = (searchText) => {
      // Фильтрация постов по тексту поиска (заголовок, текст и дата)
      const filtered = posts.filter((post) =>
        `${post.title} ${post.text} ${post.date}`.toLowerCase().includes(searchText.toLowerCase())
      );
  
      setFilteredPosts(filtered);
    };

    return (
      <div className={styles.wrapper}>
        <div className={styles.input}>
          <Input onSearch={handleSearch} />
          {/* Тернарный оператор для выбора отображаемых постов */}
          {searchText
            ? filteredPosts.map((post) => <Post key={post.id} post={post} />)
            : posts.map((post) => <Post key={post.id} post={post} />)}
        </div>
      </div>
    );
}

export default Posts;
