import React from 'react';
import { useParams } from 'react-router-dom';
import posts from '../Posts/postsData';
import './FullPost.css'

const FullPost = () => {
  const { postId } = useParams();
  const decodedTitle = postId.replace(/-/g, ' ');

  // Найдем пост с соответствующим названием
  const selectedPost = posts.find(post => post.title === decodedTitle);

  if (!selectedPost) {
    return <div>Пост не найден</div>;
  }

  const { title, imageUrl, text } = selectedPost;

  return (
    <div className="post-content">
      <h3>{title}</h3>
      <img src={imageUrl} alt={title} className="post-image" />
      <p>{text}</p>
    </div>
  );
}

export default FullPost;
