import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import Input from '../Input/Input';
import FilteredPosts from '../FilteredPosts/FilteredPosts';
import FullPost from '../FullPost/FullPost';
import postsData from './postsData';
import styles from './Posts.module.css';

const filterPosts = (allPosts, searchText) => {
  return allPosts.filter((post) =>
    post.title.toLowerCase().includes(searchText.toLowerCase()) ||
    post.text.toLowerCase().includes(searchText.toLowerCase()) ||
    post.date.toLowerCase().includes(searchText.toLowerCase())
  );
};

const Posts = () => {
  const [searchText, setSearchText] = useState('');
  const [filteredPosts, setFilteredPosts] = useState(postsData);
  const [selectedPostId, setSelectedPostId] = useState(null);
  const history = useHistory(); // Используем useHistory

  const handleSearch = (text) => {
    setSearchText(text);
    setFilteredPosts((prevPosts) => filterPosts(prevPosts, text));
  };

  const handlePostClick = (postId) => {
    setSelectedPostId(postId);
    history.push(`/posts/${postId}`);
  };

  const handleNavigateBack = () => {
    setSelectedPostId(null);
    history.push('/posts'); // Переход на страницу со всеми постами
  };

  useEffect(() => {
    setSelectedPostId(null);
  }, [searchText]);

  return (
    <div className={styles.wrapper}>
      <div className={styles.input}>
        <Input onSearch={handleSearch} />
      </div>

      {selectedPostId ? (
        <FullPost postId={selectedPostId} onNavigateBack={handleNavigateBack} />
      ) : (
        <FilteredPosts filteredPosts={filteredPosts} onPostClick={handlePostClick} />
      )}
    </div>
  );
};

export default Posts;
