import {useState, useEffect} from 'react'
import { NavLink } from 'react-router-dom'
import './Menu.css'

  const Menu = () => {
    const [isVisible, setIsVisible] = useState(true);
  
    useEffect(() => {
      const handleScroll = () => {
        setIsVisible(window.scrollY < 50); // Скрывать меню, когда прокрутка вниз меньше 50 пикселей
      };
  
      window.addEventListener('scroll', handleScroll);
  
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);

  return (
    <nav>
      <NavLink
        style={({ isActive }) =>
          isActive ? { color: 'lightyellow', textDecoration: 'none' } : {}
        }
        to="."
        end
      >
        Home
      </NavLink>
      <NavLink to="rent">Rent</NavLink>
      <NavLink
        className={({ isActive }) => (isActive ? 'activeLink' : 'link')}
        to="buysell"
      >
        BuySell
      </NavLink>
      <NavLink
        className={({ isActive }) => (isActive ? 'activeLink' : 'link')}
        to="housing"
      >
        Housing
      </NavLink>

      <NavLink
        className={({ isActive }) => (isActive ? 'activeLink' : 'link')}
        to="commercial"
      >
        Commercial
      </NavLink>
      <NavLink
        className={({ isActive }) => (isActive ? 'activeLink' : 'link')}
        to="architecture"
      >
        Architecture
      </NavLink>
      
    </nav>
  )
}

export default Menu
