import { Link } from 'react-router-dom';
import { useState } from 'react';
import styles from './Post.module.css'

const Post = ({ post }) => {
  const { imageUrl, title, text } = post;
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const shareOnInstagram = () => {
    const encodedText = encodeURIComponent(`Check out this post: ${title}`);
    const encodedUrl = encodeURIComponent(window.location.href);
    window.open(`https://www.instagram.com/share?url=${encodedUrl}&text=${encodedText}`);
  };

  const shareOnTelegram = () => {
    const encodedText = encodeURIComponent(`Check out this post: ${title}`);
    const encodedUrl = encodeURIComponent(window.location.href);
    window.open(`https://t.me/share/url?url=${encodedUrl}&text=${encodedText}`);
  };

  const shareOnFacebook = () => {
    const encodedUrl = encodeURIComponent(window.location.href);
    window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodedUrl}`);
  };

  const shareByEmail = () => {
    const encodedSubject = encodeURIComponent(`Check out this post: ${title}`);
    const encodedBody = encodeURIComponent(`I thought you might be interested in this post: ${window.location.href}`);
    window.location.href = `mailto:?subject=${encodedSubject}&body=${encodedBody}`;
  };

  return (
    <Link to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`} className={styles.postLink} onClick={toggleExpand}>
      <div className={styles.post}>
        <h3>{title}</h3>
        <img src={imageUrl} alt={title} className={styles.postImage} />
        <p>{isExpanded ? text : `${text.slice(0, 200)}...`}</p>
        
      </div>
    </Link>
  );
}

export default Post;
