import { useParams } from 'react-router-dom';
import posts from './postsData'; // Импортируйте свои данные постов или используйте API

const FullPost = () => {
  const { postId } = useParams();

  // Найдем пост с соответствующим идентификатором
  const selectedPost = posts.find(post => post.id === parseInt(postId));

  if (!selectedPost) {
    return <div>Пост не найден</div>;
  }

  const { title, imageUrl, text } = selectedPost;

  return (
    <div className="full-post">
      <h2>{title}</h2>
      <img src={imageUrl} alt={title} className="full-post-image" />
      <p>{text}</p>
    </div>
  );
}

export default FullPost;
