import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Post = ({ post }) => {
  
  const { id, imageUrl, title, text } = post;
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <Link to={`/posts/${id}`} className={`post ${isExpanded ? 'expanded' : ''}`} onClick={toggleExpand}>
      <img src={imageUrl} alt={title} className="post-image" />
      <div className="post-content">
        <h3>{title}</h3>
        <p>{isExpanded ? text : `${text.slice(0, 3)}...`}</p>
      </div>
    </Link>
  );
}

export default Post;
