import { useParams } from 'react-router-dom';
import Post from '../Post/Post';
import posts from '../Posts/postsData'; // Импортируйте свои данные постов или используйте API
im
const FullPost = () => {
  const { postId } = useParams();
  const selectedPost = posts.find(post => post.id === parseInt(postId));

  if (!selectedPost) {
    return <h3>Пост не найден</h3>;
  }

  const { title, imageUrl, text } = selectedPost;

  return (
    <div className="post">
      <h3>{title}</h3>
      <img src={imageUrl} alt={title} className="post-image" />
      <p>{text}</p>
      {/* Рендерим компонент Post с полным текстом для сравнения */}
      <Post post={selectedPost} showFullPost={() => {}} />
    </div>
  );
}

export default FullPost;
