import React from 'react';
import { useSpring, animated } from 'react-spring';
import styles from './Header.module.css';

const Header = () => {
  const { textOpacity } = useSpring({
    from: { textOpacity: 0 },
    to: { textOpacity: 1 },
    config: { duration: 2000 }, // Можете настроить продолжительность анимации по вашему усмотрению
  });

  const fullText = 'Wrocław Dom. Twój przewodnik w świecie nieruchomości';

  return (
    <header className={styles.header}>
      <img
        className={styles.headerBackground}
        src="./img/layout/wroclaw1.jpg"
        alt="Wroclaw"
      />
      <animated.p style={{ ...textOpacity }} className={`${styles.logo} ${styles.headerText}`}>
        {fullText}
      </animated.p>
    </header>
  );
}

export default Header;
