import React from 'react';
import { useParams } from 'react-router-dom';
import posts from '../Posts/postsData';
import './FullPost.css'

const FullPost = () => {
  

  // Преобразуем postId к числу
  const selectedPost = posts.find(post => post.id === parseInt(postId));

  console.log('selectedPost:', selectedPost); // Отладочный вывод

  if (!selectedPost) {
    return <h3>Пост не найден</h3>;
  }

  const { title, imageUrl, text } = selectedPost;

  return (
    <div className="post-content">
      <h3>{title}</h3>
      <img src={imageUrl} alt={title} className="post-image" />
      <p>{text}</p>
    </div>
  );
}

export default FullPost;
